mapper.update:
update goods_import_detail set updated_time = now()

    ,spu_name = #{spuName}


    ,size = #{size}


    ,color = #{color}


    ,type = #{type}


    ,feature1 = #{feature1}


    ,feature2 = #{feature2}


    ,feature3 = #{feature3}


    ,count = #{count}


    ,in_price = #{inPrice}


    ,sale_price = #{salePrice}


    ,is_deleted = #{isDeleted}

where sku_id =#{skuId};

mapper.select:
select sku_id, spu_id, spu_name, size, color, type, b.type_name, feature1, feature2, feature3, in_price, sale_price, count, a.created_time, a.updated_time from goods_import_detail a,goods_type b where a.type = b.id and a.is_deleted = 0
and spu_name like "%"#{spuName}"%"
and type = #{type}
and a.created_time >= #{createdTimeStart}
and a.created_time <= #{createdTimeEnd}
limit #{offset},#{limit}

select
count(1)
from goods_import_detail a,goods_type b
where a.type = b.id and a.is_deleted = 0

    and spu_name like "%"#{spuName}"%"


    and type = #{type}


    and a.created_time <= #{createdTimeStart}


    and a.created_time >= #{createdTimeEnd}
