mapper.update:
update sku set updated_time = now()

    ,spu_id = #{spuId}


    ,size = #{size}


    ,color = #{color}


    ,feature1 = #{feature1}


    ,feature2 = #{feature2}


    ,feature3 = #{feature3}


    ,is_deleted = #{isDeleted}


    ,count = count + #{count}

where sku_id =#{skuId};

update.foreach:
update sku set updated_time = NOW()

    ,size = #{item.size}


    ,color = #{item.color}


    ,feature1 = #{item.feature1}


    ,feature2 = #{item.feature2}


    ,feature3 = #{item.feature3}


    ,count = count + #{item.count}

where sku_id =#{item.skuId}

mapper.select:
select p.spu_id, p.spu_name, p.sale_price, g.type_name, k.size, k.color, k.count, k.feature1, k.feature2, k.feature3, k.created_time, k.updated_time from spu p,sku k,goods_type g where p.spu_id = k.spu_id and p.type = g.id and p.is_deleted = 0 and k.is_deleted = 0
and p.spu_name like "%"#{spuName}"%"
and p.type = #{type}
limit #{offset},#{limit}

select count(1) from spu p,sku k,goods_type g where p.spu_id = k.spu_id and p.type = g.id and p.is_deleted = 0 and k.is_deleted = 0
and p.spu_name like "%"#{spuName}"%"
and p.type = #{type}
