mapper.update:
update spu set updated_time = now()

    ,spu_name = #{spuName}


    ,in_price = #{inPrice}


    ,sale_price = #{salePrice}


    ,total_count = #{totalCount}

where spu_id = #{spuId};

mapper.select:
select spu_id, spu_name, in_price, sale_price, total_count, created_time, updated_time from spu
spu_name like "%"#{spuName}"%"
limit #{offset},#{pageSize}
